+    justify-content: center;
+    flex-direction: column;
+  }
+
+  #form-signup {
+    width: fit-content;
+    display: flex;
+    flex-direction: column;
+    justify-content: center;
+    margin: 0 auto;
+    gap: 1rem;
+  }
+  h2 {
+    text-align: center;
+  }
+</style>

